//Importa a função useState do React. Ela é um hook que permite adicionar estado a componentes de função.
import { useState } from "react";
import axios from 'axios';

function Login() {
  //Usa o hook useState para criar estados para email e senha, ambos inicializados com uma string vazia.
  //setEmail e setSenha são funções que atualizam os estados de cada um.
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [error, setError] = useState(''); //cria um estado para armazenar o erro de login
  
  //Define uma função assíncrona chamada "handleLogin" que manipula o evento de clique
  //no botão de login.
  const handleLogin = async(evento) => {
    //impede o comportamento padrão do evento clique, que seria o envio dos dados do formulário
    //para o servidor, resultando em uma recarga completa da página. Com o "preventDefault()" a
    //página não será recarregada, e os dados do formulário não serão enviados para o servidor.
    evento.preventDefault();

    //Exibe no console do navegador os valores de email e senha, que foram atualizados pelos
    //campos input do formulário.
    console.log (email, senha);
    try {
      const response = await axios.post('http://localhost:3000/login',
        JSON.stringify ({email, senha}),
        {headers: {'Content-Type': 'application/json'}});
    } catch (error) {
      if (!error?.response) {
        setError(true);
      }
    }
    
    const response = await axios.post('http://localhost:3000/login', JSON.stringify ({email, senha}),
     {Headers: {'Content-Type': 'application/json'}});
  };
  
  //Define a estrutura do componente de login. Composta de um formulário com dois campos "input"
  //de entrada de e-mail e senha, e um botão de login.
  return (
    
    //cria a div principal que vai conter todo o formulario
    <div className = 'container_Formulario_Login'>
      
      <h2>Login</h2> {/*cria o titulo "Login"*/}

      {/*cria um formulario para conter os dados que serão enviados ao servidor*/}
      <form className = 'formulario_Login'> 
        
        {/*cria os campos input e define "placeholder" para ambos */}
        {/*onChange é um evento que ocorre quando o valor do campo é alterado.*/}
        {/*onChange={(evento) => setEmail(evento.target.value)} é uma função que recebe um evento e atualiza o estado 
        "email" com o valor do campo input.*/}
        {/*onChange={(evento) => setSenha(evento.target.value)} é uma função que recebe um evento e atualiza o estado
         "senha" com o valor do campo input.*/}
        <input type="email" name="email" placeholder= "email" required onChange={(evento) => setEmail(evento.target.value)}/>
        <input type="password" name="senha" placeholder = "senha" required onChange={(evento) => setSenha(evento.target.value)}/>

        {/*cria um botão padrão para enviar os dados. Quando o botão é acionado (pelo cique do mouse ou enter
        no teclado), o "onClick" chama a função handleLogin, e passa pra ela o parâmetro "evento", que neste caso
        é o "clique".*/}
        <button type="submit" className='btn-login' onClick = {(evento) => handleLogin(evento)}>Login</button>
      </form>
    </div>
  );
}

//Permite a exportação do componente Login para que possa ser usado em outros arquivos.
export default Login;
