import axios from "axios";
import { useState } from "react"; //Importa a função useState do React. Ela é um hook que permite adicionar estado a componentes de função.

function Login() {
  //Usa o hook useState para criar estados para 4 propriedades (email, senha, error e usuário).
  //setEmail, setSenha, setError e setUsuario são funções que atualizam os estados de cada um.
  const [email, setEmail] = useState(''); //cria um estado para armazenar o email, e o inicializa com uma string vazia.
  const [senha, setSenha] = useState(''); //cria um estado para armazenar a senha, e o inicializa com uma string vazia.
  const [error, setError] = useState(''); //cria um estado para armazenar o erro de login, e o inicializa com uma string vazia.
  const [usuario, setUsuario] = useState(null); //cria um estado para armazenar o usuario, e o inicializa com nulo.
  
  //Define uma função assíncrona chamada "handleLogin" que manipula o evento de clique
  //no botão de login.
  const handleLogin = async(evento) => {
    //impede o comportamento padrão do evento clique, que seria o envio dos dados do formulário
    //para o servidor, resultando em uma recarga completa da página. Com o "preventDefault()" a
    //página não será recarregada, e os dados do formulário não serão enviados para o servidor.
    evento.preventDefault();

    try { //Inicia um bloco de código onde a execução normal tentará ser executada.
      const response = await axios.post( //Envia solicitação de login ao servidor utilizando Axios. Se tiver sucesso,
                                         //armazena a resposta da solicitação na variavel "response".
        'http://localhost:3000/login', //endereço do servidor axios
        JSON.stringify({ email, senha }), //dados a serem enviados para o servidor
        { headers: { 'Content-Type': 'application/json' } } //configuração do cabeçalho da solicitação (envio padrão)
      );

      setUsuario(response.data); //

    } catch (error) {
      // Se ocorrer um erro durante a solicitação:
      if (!error?.response) {
        // Se não houver uma resposta do servidor, define uma mensagem de erro relacionada ao acesso ao servidor.
        setError('Erro ao acessar o servidor');
      } else if (error.response.status === 401) {
        // Se a resposta do servidor indicar um status 401 (Unauthorized), define uma mensagem de erro indicando usuário ou senha inválidos.
        setError('Usuário ou senha inválidos');
      }
    }
  };

  const handleLogout = () => {
    setUsuario(null);
    setError('');
  };
  
  
  //Define a estrutura do componente de login. Composta de um formulário com dois campos "input"
  //de entrada de e-mail e senha, e um botão de login.
  return (
    
    //cria a div principal contendo o formulario, se o usuario for nulo. Se não for
    //se não for nulo (já foi autenticado), exibe uma mensagem de bem-vindo e um botão para sair.
    <div className = 'container_Formulario_Login'>
      {usuario == null ? (
        <div>
          <h2>Login</h2> {/*cria o titulo "Login"*/}
          {/*cria um formulario para conter os dados que serão enviados ao servidor*/}
          <form className = 'formulario_Login'> 

            {/*cria os campos input e define "placeholder" para ambos */}
            {/*onChange é um evento que ocorre quando o valor do campo é alterado.*/}
            {/*onChange={(evento) => setEmail(evento.target.value)} é uma função que recebe um evento e atualiza o estado 
            "email" com o valor do campo input.*/}
            {/*onChange={(evento) => setSenha(evento.target.value)} é uma função que recebe um evento e atualiza o estado
            "senha" com o valor do campo input.*/}
            <input type="email" name="email" placeholder= "email" required onChange={(evento) => setEmail(evento.target.value)}/>
            <input type="password" name="senha" placeholder = "senha" required onChange={(evento) => setSenha(evento.target.value)}/>

            {/*cria um botão padrão para enviar os dados. Quando o botão é acionado (pelo cique do mouse ou enter
            no teclado), o "onClick" chama a função handleLogin, e passa pra ela o parâmetro "evento", que neste caso
            é o "clique".*/}
            <button type="submit" className='btn-login' onClick = {(evento) => handleLogin(evento)}>Login</button>
          </form>
          <p>{error}</p>
        </div>
      ) : (
          <div id="div_Logado">
            <h2>Olá {usuario.nome}, vá pra tela de João</h2>
            <button type="button" className='btn_Logado' onClick={() => handleLogout()}>Sair</button>
          </div>
      )}
    </div>
  );
}

//Permite a exportação do componente Login para que possa ser usado em outros arquivos.
export default Login;
