import axios from "axios"; //Importa a biblioteca Axios para realizar solicitações HTTP ao servidor.
import { useState } from "react"; //Importa a função useState do React. Ela é um hook que permite adicionar estado a componentes de função.

function Login() {//Cria o componente Login. Ele é uma função que retorna um elemento JSX.
  const [email, setEmail] = useState(''); 
  const [senha, setSenha] = useState(''); 
  const [error, setError] = useState(''); 
  const [usuario, setUsuario] = useState(null); //cria um estado para armazenar o usuario, e o inicializa com nulo.
  
  const handleLogin = async(evento) => { //cria uma função assíncrona que recebe um evento (click) como parâmetro.
    evento.preventDefault(); //impede a ação padrão do evento clique (envio dos dados do formulário e recarregamento da página).

    try { //Tenta executar o código abaixo. Se ocorrer um erro, o bloco catch será executado.
      const response = await axios.post( // Envia uma solicitação de login para o servidor utilizando Axios.
        'http://localhost:3000/login', //URL do servidor que receberá a solicitação.
        JSON.stringify({ email, senha }), //Dados que serão enviados para o servidor. Neste caso, o email e a senha.
        { headers: { 'Content-Type': 'application/json' }}); //Configura o cabeçalho padrão da solicitação JSON.
    
      setUsuario(response.data); // Se a solicitação for bem-sucedida, atualiza o estado "usuario" com os dados do usuário.
    
    } catch (error) { // Se ocorrer um erro durante a solicitação:
      if (!error?.response) { //Se a resposta do servidor foi numa ou indefinida.
        setError('Erro ao acessar o servidor'); //Define uma mensagem de erro que não foi possível acessar o servidor.
      } else if (error.response.status === 401) {//Se o servidor respondeu um status 401 (Não autorizado)
        setError('Usuário ou senha inválidos'); //Define uma mensagem de erro que o usuário ou senha são inválidos.
      }
    }
      };

  const handleLogout = () => {
    setUsuario(null);
    setError('');
  };
  
  
  //Define a estrutura do componente de login. Composta de um formulário com dois campos "input"
  //de entrada de e-mail e senha, e um botão de login.
  return (
    
    //cria a div principal contendo o formulario, se o usuario for nulo. Se não for
    //se não for nulo (já foi autenticado), exibe uma mensagem de bem-vindo e um botão para sair.
    <div className = 'container_Formulario_Login'>
      {usuario == null ? (
        <div>
          <h2>Login</h2> {/*cria o titulo "Login"*/}
          {/*cria um formulario para conter os dados que serão enviados ao servidor*/}
          <form className = 'formulario_Login'> 

            {/*cria os campos input e define "placeholder" para ambos */}
            {/*onChange é um evento que ocorre quando o valor do campo é alterado.*/}
            {/*onChange={(evento) => setEmail(evento.target.value)} é uma função que recebe um evento e atualiza o estado 
            "email" com o valor do campo input.*/}
            {/*onChange={(evento) => setSenha(evento.target.value)} é uma função que recebe um evento e atualiza o estado
            "senha" com o valor do campo input.*/}
            <input type="email" name="email" placeholder= "email" required onChange={(evento) => setEmail(evento.target.value)}/>
            <input type="password" name="senha" placeholder = "senha" required onChange={(evento) => setSenha(evento.target.value)}/>

            {/*cria um botão padrão para enviar os dados. Quando o botão é acionado (pelo cique do mouse ou enter
            no teclado), o "onClick" chama a função handleLogin, e passa pra ela o parâmetro "evento", que neste caso
            é o "clique".*/}
            <button type="submit" className='btn-login' onClick = {(evento) => handleLogin(evento)}>Login</button>
          </form>
          <p>{error}</p>
        </div>
      ) : (
          <div id="div_Logado">
            <h2>Olá {usuario.nome}, vá pra tela de João</h2>
            <button type="button" className='btn_Logado' onClick={() => handleLogout()}>Sair</button>
          </div>
      )}
    </div>
  );
}

//Permite a exportação do componente Login para que possa ser usado em outros arquivos.
export default Login;
