// Definição do componente funcional InputField. 
// Este componente recebe várias propriedades (props) que são passadas para ele quando é usado.
function InputField({ type, name, label, value, onChange, onFocus }) {
  // O componente retorna JSX, que é uma sintaxe semelhante ao HTML para definir a estrutura do componente.
  return (
    // Um div que envolve todo o input e sua label. 
    // A classe do div é dinamicamente determinada pelo nome do campo.
    // Se o campo for 'nome', a classe será 'grupo_Input1', caso contrário, será 'grupo_Input2'.
    <div className={`grupo_Input${name === 'nome' ? '1' : '2'}`}>
      
      {/* O elemento input do HTML é usado aqui.
      As props 'type', 'id', 'name', 'value', 'onChange' e 'onFocus' são passadas para o input.
      'type' define o tipo do input (ex: text, password).
      'id' e 'name' são identificadores padrão para o input, acho que não os usei.
      'value' contém o valor atual do input, tornando este um componente controlado.
      'onChange' é um manipulador de eventos que é acionado quando o valor do input muda.
      'onFocus' é acionado quando o input ganha foco. */}
      <input type={type} id={name} name={name} required value={value}
             onChange={onChange} onFocus={onFocus} />

      {/* Um div para a label flutuante do input.
      A classe deste div também é determinada dinamicamente pelo nome do campo.
      Se o campo for 'nome', a classe será 'label_Contorno_Nome', caso contrário, será 'label_Contorno_Senha'.
      O conteúdo da label é definido pela prop 'label'. */}
      <div className={`label_Contorno_${name === 'nome' ? 'Nome' : 'Senha'}`}>
        {label}
      </div>
    </div>
  );
}

// Exporta o componente InputField para que possa ser usado em outros arquivos.
export default InputField;
