function Login() {
  //Define uma função assíncrona chamada "handleLogin" que manipula o evento de clique
  //no botão de login.
  const handleLogin = async(evento) => {

    //impede o comportamento padrão do evento clique, que seria o envio dos dados do formulário
    //para o servidor, resultando em uma recarga completa da página. Com o "preventDefault()" a
    //página não será recarregada, e os dados do formulário não serão enviados para o servidor.
    evento.preventDefault();
    console.log ('login clicked');
  }
  
  //Define a estrutura do componente de login. Composta de um formulário com dois campos "input"
  //de entrada de e-mail e senha, e um botão de login.
  return (

    <div className = 'container_Form'>
      <h2>Login</h2>
      <form className = 'login-form'>
        <input type="email" name="email" placeholder= "email" required/>
        <input type="senha" name="senha" placeholder = "senha" required/>
        <button type="submit" className='btn-login' 
        onClick = {(evento) => handleLogin(evento)}>Login</button>
      </form>
    </div>
  );
}

export default Login;
