import axios from 'axios'; // Importa a biblioteca Axios para fazer requisições HTTP.
import { useState } from 'react'; // Importa o hook useState do React para gerenciar o estado local.
import Logo from './Identidade_Visual/logotipo-branco.png'; // Importa uma imagem de logo do diretório especificado.

function Login() {
  // Define estados locais para gerenciar nome, senha, mensagem de erro e informações do usuário.
  const [nome, setNome] = useState('');
  const [senha, setSenha] = useState('');
  const [error, setError] = useState('');
  const [usuario, setUsuario] = useState(null);

  // Função para lidar com o processo de login.
  const handleLogin = async (evento) => {
    evento.preventDefault(); // Previne o comportamento padrão do formulário (recarregar a página).

    try {
      // Faz uma requisição POST para o servidor com os dados de login.
      const response = await axios.post(
        'http://localhost:3000/login',
        JSON.stringify({ nome, senha }),
        { headers: { 'Content-Type': 'application/json' }}
      );

      // Atualiza o estado com os dados do usuário em caso de sucesso.
      setUsuario(response.data);

    } catch (error) {
      // Lida com possíveis erros na requisição.
      if (!error?.response) {
        setError('Erro ao acessar o servidor');
      } else if (error.response.status === 401) {
        setError('Usuário ou senha inválidos');
      }
    }
  };

  // Função para lidar com o processo de logout.
  const handleLogout = () => {
    setUsuario(null); // Reseta o estado do usuário para nulo.
    setError(''); // Limpa possíveis mensagens de erro.
  };

  return (
    <div className='login-container'>
      <div className='login-image'>
        <img src={Logo} alt='Logo'></img>
        {/* ... código da interface ... */}
      </div>
      {usuario == null ? ( // Renderiza condicionalmente o formulário de login ou a mensagem de boas-vindas.
        <div>
          {/* ... código do formulário de login ... */}
        </div>
      ) : (
        <div id='div_Logado'>
          <h2>Olá {usuario.nome}, você logou como {usuario.nivel}</h2>
          <button type='button'
                  className='btn_Logado'
                  onClick={() => handleLogout()}>Sair</button>
        </div>
      )}
    </div>
  );
}

export default Login; // Exporta o componente Login para uso em outros arquivos.
