function Login() {
  //Define uma função assíncrona chamada "handleLogin" que manipula o evento de clique
  //no botão de login.
  const handleLogin = async(evento) => {

    //impede o comportamento padrão do evento clique, que seria o envio dos dados do formulário
    //para o servidor, resultando em uma recarga completa da página. Com o "preventDefault()" a
    //página não será recarregada, e os dados do formulário não serão enviados para o servidor.
    evento.preventDefault();
    console.log ('login clicked');
  }
  
  //Define a estrutura do componente de login. Composta de um formulário com dois campos "input"
  //de entrada de e-mail e senha, e um botão de login.
  return (
    
    //cria a div principal que vai conter todo o formulario
    <div className = 'container_Formulario_Login'>
      
      <h2>Login</h2> {/*cria o titulo "Login"*/}

      {/*cria um formulario para conter os dados que serão enviados ao servidor*/}
      <form className = 'formulario_login'> 
        
        {/*cria os campos input e define "placeholder" para ambos */}
        <input type="email" name="email" placeholder= "email" required/>
        <input type="password" name="senha" placeholder = "senha" required/>

        {/*cria um botão padrão para enviar os dados. Quando o botão é acionado (seja pelo
        clique do mouse ou enter no teclado) o "onClick" chama a função handleLogin, e 
        passa pra ela o parâmetro "evento", que neste caso é
        o .*/}
        <button type="submit" className='btn-login' onClick = {(evento) => handleLogin(evento)}>Login</button>
      </form>
    </div>
  );
}

//Exporta o componente Login para que ele possa ser usado em outros arquivos.
export default Login;
